import React, { Component } from 'react';
import API from '../../utils/API';
import CanvasJSReact from '../../utils/canvasjs.react';
import { CurrentUsage } from '../../components/CurrentUsage';

var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class Home extends Component{
  state = {
    usage: [],
    perMinute: true
  }

  componentDidMount(){
    API.get('/usage').then(result =>
      this.setState(() => ({
        usage: result.data
      }))
    );
  }

  changeToHourly = event => {
    let button = event.target;
    API.get('/usage/hourly').then(result =>
      this.setState(() => ({
        usage: result.data
      }))
    );
    button.innerHTML = "Ver por minuto";
  }

  render() {
    const { usage } = this.state;
    const lastUpdate = usage[usage.length - 1];
    usage.forEach(e => {e.x = new Date(e.x)});
    console.log(usage);
		const options = {
			animationEnabled: true,
			title:{
				text: "Uso de bicicletas Bike Santiago"
			},
			axisX: {
        valueFormatString: "hh:mm",
        title: "Por minuto"
			},
			axisY: {
				title: "Bicicletas utilizadas",
        includeZero: false
			},
			data: [{
				xValueFormatString: "DD MMM hh:mm",
				type: "spline",
				dataPoints: usage
			}]
		}
		return (
		<div>
      {lastUpdate ? (
        <CurrentUsage lastUpdate={lastUpdate} />
      ) : (
        <div></div>
      )
      }
			<CanvasJSChart options = {options} />
      <button type="button" onClick={this.changeToHourly}>
        Ver por hora
      </button>
		</div>
		);
	}
};

export default Home;
